mapper.update:
UPDATE user
SET created_at = now()
WHERE email = #{email}

UPDATE user
SET provider_id = #{providerId},
Name = #{name}

    , birthday = #{birthday}

, profile_img_url = #{profileImgUrl}
WHERE email = #{email}

mapper.select:
SELECT COUNT(*) > 0
FROM user
WHERE email = #{email}

SELECT id
FROM user
WHERE email = #{email}
